def get_random_book(genre=None):
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç —Å–ª—É—á–∞–π–Ω—É—é –∫–Ω–∏–≥—É"""
    if genre and genre in BOOKS_DATABASE:
        books = BOOKS_DATABASE[genre]
    else:
        # –í—Å–µ –∫–Ω–∏–≥–∏
        books = []
        for genre_books in BOOKS_DATABASE.values():
            books.extend(genre_books)
    
    if books:
        return random.choice(books)
    return None

def get_all_genres():
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –≤—Å–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ –∂–∞–Ω—Ä—ã"""
    return list(BOOKS_DATABASE.keys())

def get_books_by_genre(genre):
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –≤—Å–µ –∫–Ω–∏–≥–∏ —É–∫–∞–∑–∞–Ω–Ω–æ–≥–æ –∂–∞–Ω—Ä–∞"""
    return BOOKS_DATABASE.get(genre, [])

def search_books(keyword):
    """–ò—â–µ—Ç –∫–Ω–∏–≥–∏ –ø–æ –∫–ª—é—á–µ–≤–æ–º—É —Å–ª–æ–≤—É"""
    results = []
    keyword = keyword.lower()
    
    for genre, books in BOOKS_DATABASE.items():
        for book in books:
            if (keyword in book["–Ω–∞–∑–≤–∞–Ω–∏–µ"].lower() or 
                keyword in book["–∞–≤—Ç–æ—Ä"].lower() or
                keyword in book["–æ–ø–∏—Å–∞–Ω–∏–µ"].lower()):
                results.append((genre, book))
    
    return results